import React, { useState } from 'react'

const ToDoList = () => {
    const [tasks, setTasks] = useState([
        { title: 'Buy bread' },
        { title: 'Celebrate' },
        { title: 'Create site' }
    ])
    const addTask = () => {
        const tasksCopy = [...tasks];
        tasksCopy.push({title:'New task'});
        setTasks(tasksCopy);
    }
    return (
        <div>
            <input type="text"/>
            <button onClick={() => addTask()}>Add</button>
            <ul>
                {tasks.map((el, i) => {
                    return <li key={i}>{el.title}</li>
                })}
            </ul>
        </div>
    )
}

export default ToDoList
