import { useSelector, useDispatch } from 'react-redux'

function App() {
  const tasks = useSelector((state) => state.tasks);

  const add = () => {

    console.log(123);
  }

  return (
    <div className="App">
      <ul>
        {tasks.map((task)=>{
          <li>{task.title}</li>
        })}
      </ul>
      <button onClick={add}>Add task</button>
    </div>
  );
}

export default App;
